import React, { useState, useContext } from 'react';

import { CopyToClipboard } from 'react-copy-to-clipboard';


import { SocketContext } from '../Context';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexDirection: 'column',
  },
  gridContainer: {
    width: '100%',
    [theme.breakpoints.down('xs')]: {
      flexDirection: 'column',
    },
  },
  container: {
    width: '600px',
    margin: '35px 0',
    padding: 0,
    [theme.breakpoints.down('xs')]: {
      width: '80%',
    },
  },
  margin: {
    marginTop: 20,
  },
  padding: {
    padding: 20,
  },
  paper: {
    padding: '10px 20px',
    border: '2px solid black',
  },
}));

const Sidebar = ({ children }) => {
  const { me, callAccepted, name, setName, callEnded, leaveCall, callUser } = useContext(SocketContext);
  const [idToCall, setIdToCall] = useState('');
  const classes = useStyles();

  return (
    <div className={classes.container}>
      <div elevation={10} className={classes.paper}>
        <form className={classes.root} noValidate autoComplete="off">
          <div container className={classes.gridContainer}>
            <div item xs={12} md={6} className={classes.padding}>
              <h1 gutterBottom variant="h6">Account Info</h1>
              <input label="Name" value={name} onChange={(e) => setName(e.target.value)} fullWidth />
              <CopyToClipboard text={me} className={classes.margin}>
                <button variant="contained" color="primary" fullWidth startIcon={<Assignment fontSize="large" />}>
                  Copy Your ID
                </button>
              </CopyToClipboard>
            </div>
            <div item xs={12} md={6} className={classes.padding}>
              <h1 gutterBottom variant="h6">Make a call</h1>
              <input label="ID to call" value={idToCall} onChange={(e) => setIdToCall(e.target.value)} fullWidth />
              {callAccepted && !callEnded ? (
                <button variant="contained" color="secondary" fullWidth onClick={leaveCall} className={classes.margin}>
                  Hang Up
                </button>
              ) : (
                <button variant="contained" color="primary" startIcon={<Phone fontSize="large" />} fullWidth onClick={() => callUser(idToCall)} className={classes.margin}>
                  Call
                </button>
              )}
            </div>
          </div>
        </form>
        {children}
      </div>
    </div>
  );
};

export default Sidebar;
